import styled from 'styled-components';

export const Container = styled.div`
  padding: 30px;
  margin: 0 auto;
`;

export const Title = styled.h1`
  margin: 15px;
  font-weight: 700;
  font-size: 25px;
  line-height: 25px;
  letter-spacing: 0.03em;
`;

export const Subtitle = styled.h2`
  margin: 15px;
  font-weight: 600;
  font-size: 22px;
  line-height: 25px;
  letter-spacing: 0.03em;
`;
