import styled from 'styled-components';

export const Form = styled.form`
  display: flex;
  flex-direction: column;
  border: 1px solid #000;
  width: 350px;
  height: 160px;
  padding: 15px;
`;

export const Input = styled.input`
  display: block;
  width: 50%;
  margin-top: 5px;
  margin-bottom: 20px;
`;

export const Button = styled.button`
  width: 100px;
  margin-top: 5px;
`;
